mapper.select:
SELECT

FROM proj_info
WHERE 1=1

  AND n_or_e = #{projFlag}


  AND city_id = #{cityId}


  AND district_id = #{districtId}


  AND area_id = #{areaId}


  AND  projname LIKE CONCAT('%', #{keyword}, '%')


  AND  projname LIKE CONCAT('%', #{projName}, '%')


  AND  is_approve = #{isApprove}


ORDER BY update_time DESC
